/**
 * 图表基类
 */
class Chart {
    constructor(container) {
        this.container = container;//canvas容器
        this.canvas = document.createElement('canvas');
        this.ctx = this.canvas.getContext('2d');
        this.W = 500; //画布宽
        this.H = 500; //画布高
        this.paddingTop = 0;//canvas容器距上高度（为标题留空白）
        this.paddingBottom = 30;
        this.title = '';//散点图的标题
        this.legend = []; //图例
        this.series = []; //坐标系中的点数据
        this.xAxis = {}; //x轴信息
        this.yAxis = {}; //y轴信息
        this.animateArr = [];
        this.mapDataArr = [];//被处理后的整个地图数据
        this.info = {};
        this.maxScale = 8;
        this.scaleSize = 1.2;//每次放大倍数
        this.moveScale = 0.1;
        this.translateX = 0;
        this.translateY = 0;
        this.scale = 1;//地图放大倍数
        this.matrix = new Matrix();

    }
    init(opt) {
        Object.assign(this, opt);
        if (!this.container) return;
        this.W *= 2;
        this.H *= 2;
        this.canvas.width = this.W;
        this.canvas.height = this.H;
        this.canvas.style.width = this.W / 2 + 'px';
        this.canvas.style.height = this.H / 2 + 'px';
        this.container.style.position = 'relative';
        this.tip = document.createElement('div');
        this.tip.style.cssText = 'display: none; position: absolute; opacity: 0.5; background: #000; color: #fff; border-radius: 5px; padding: 5px; font-size: 8px; z-index: 99;';
        this.container.appendChild(this.canvas);
        this.container.appendChild(this.tip);
        this.create(true);
        this.addEvent();
    }
    showTip(pos, title, arr) {
        var box = this.canvas.getBoundingClientRect(),
            con = this.container.getBoundingClientRect(),
            html = '',
            txt = '';
        html += '<p>' + title + '</p>';
        arr.forEach(obj => {
            txt = this.yAxis.formatter ? this.yAxis.formatter.replace('{value}', obj.num) : obj.num;
            html += '<p>' + obj.name + ': ' + txt + '</p>';
        })
        this.tip.innerHTML = html;
        this.tip.style.left = (pos.x + (box.left - con.left) + 15) + 'px';
        this.tip.style.top = (pos.y + (box.top - con.top) + 15) + 'px';
        this.tip.style.display = 'block';
    }
}

class Point {
    constructor(params) {
        this.x = params.x;
        this.y = params.y;
        this.productId = params.productId;

    }

}

/**
 * 散点图
 */
class Map extends Chart {
    constructor(container) {
        super(container);
        this.xAxisStyle = {
            fillStyle: '#000',
            strokeStyle: 'red',
            lineWidth: 1,
            textAlign: 'center',
            textBaseLine: 'middle',
            font: '18px arial'
        }
        this.yAxisStyle = {
            fillStyle: '#000',
            strokeStyle: 'red',
            lineWidth: 1,
            textAlign: 'center',
            textBaseLine: 'middle',
            font: '18px arial'
        }
    }
    beLarger(evt) {
        let that = this,
            ctx = this.ctx,
            maxScale = this.maxScale,
            scaleSize = this.scaleSize,
            currentScale = this.scale;

        let box = that.canvas.getBoundingClientRect(),
            pos = {
                x: evt.srcEvent.clientX - box.left,
                y: evt.srcEvent.clientY - box.top
            },
            { x, y } = pos;

        if (currentScale < maxScale) {
            currentScale = this.scale = currentScale * scaleSize;//放大scaleSize 倍
            if (currentScale > maxScale) {
                currentScale = this.scale = maxScale
            }
            // 先缩放
            this.matrix.scale(scaleSize, scaleSize);
            // 获得原点偏移量
            let {e, f} = this.matrix;
            let originX = e;
            let originY = f;
            // 计算点击点的偏移量
            let deltaX = (x - originX) * (this.scale - 1);
            let deltaY = (y - originY) * (this.scale - 1);
            // 再平移一下坐标
            this.matrix.translate(-deltaX, -deltaY);
            let {a, b, c, d} = this.matrix;
            let e1 = this.matrix.e;
            let f1 = this.matrix.f;
            // 重新设置 canvas 的变换矩阵
            this.ctx.setTransform(a, b, c, d, e1, f1);
            this.refresh();
        } else { //最大了
            alert("最大了，不能放大了！")
        }
        //}

    }
    addEvent() {
        let that = this,
            ctx = that.ctx,
            canvas = that.canvas,
            W = this.W,
            H = this.H,
            xLength = this.W,
            xl = this.xAxis.data.length,//被分成多少段
            imgH = 57,
            imgW = 51,
            currentScale = this.scale,//当前放大倍数
            xs = xLength / xl;//x每段长度

        let panCount = 0;//移动次数
        let hammerDom = document.querySelector("canvas"),
            hammer = new Hammer(hammerDom),
            hammerManager = new Hammer.Manager(hammerDom, {
                recognizers: [
                    [Hammer.Pinch, {
                        enable: true
                    }]
                ]
            });

        if (!this.series.length) return;//没有数据（没有点）

        //自定义单双击事件
        hammerManager.add(new Hammer.Tap({ event: 'doubletap', taps: 2 }));
        hammerManager.add(new Hammer.Tap({ event: 'singletap' }));
        //双击时，单击失效；单击不受影响
        hammerManager.get('doubletap').recognizeWith('singletap');
        hammerManager.get('singletap').requireFailure('doubletap');

        hammerManager.on("doubletap", function (e) {
            that.beLarger(e);
        })
        hammerManager.on("singletap", function (e) {
            e.preventDefault()
            let box = that.canvas.getBoundingClientRect(),
                pos = {
                    x: e.srcEvent.clientX - box.left,
                    y: e.srcEvent.clientY - box.top
                }
            console.log("当前点坐标", pos.x, pos.y, "平移距离:", that.translateX, that.translateY, "放大倍数:" + that.scale);
            //当前点击点的坐标是（translateX+pos.x,translateY+pos.y ）
            //判断点击的点在坐标网格中(*2 是因为canvas放大了两倍)
            if (2 * pos.x > 0 && 2 * pos.x < W && pos.y * 2 > that.paddingTop && pos.y * 2 < H - that.paddingBottom) {
                let arr = [] //存放点中点的信息
                //TODO 创建一个完整的数据json
                for (let i = 0, item; i < that.mapDataArr.length; i++) {
                    item = that.mapDataArr[i]

                    for (let j = 0, obj; j < item.data.length; j++) {
                        obj = item.data[j]
                        //ctx.beginPath()
                        //ctx.fillStyle = 'rgba(0,0,0,0.1)'
                        ctx.rect(obj.x * currentScale - imgW / currentScale / 2, obj.y * currentScale - imgH / currentScale, imgW / currentScale, imgH / currentScale)
                        //(x * currentScale - makerW / currentScale / 2)
                        //console.log(i,j,ctx.isPointInPath(pos.x, pos.y))

                        if (ctx.isPointInPath(pos.x * 2 * currentScale, pos.y * 2 * currentScale)) {
                            arr.push(obj);
                            that.clearMap({ i: i, j: j });
                            console.log(i, j, obj.x, obj.y)

                            break;
                        }
                    }
                    if (arr.length) {//点中了点跳出循环
                        break;
                    }
                }
                if (arr.length) {

                } else {//点击在坐标系中非标注点
                    that.clearMap();
                }
            } else {
                //不在网格中的处理
                that.clearMap();
                //return;
            }
        })
        //this.canvas.addEventListener("click", , false)//冒泡过程中执行处理函数
        hammer.on("pan", (evt) => {
            let deltaX = evt.deltaX * this.moveScale; //x方向滑动距离
            let deltaY = evt.deltaY * this.moveScale; //y方向滑动距离
            evt.preventDefault();
            this.matrix.translate(deltaX, deltaY);
            let {a, b, c, d, e, f} = this.matrix;
            this.ctx.setTransform(a, b, c, d, e, f);
            this.refresh();
        })
    }

    create() {
        this.drawX();
        this.drawY();
        this.drawMarker();
    }

    clear() {
        let { ctx } = this;
        ctx.save();
        // 为了清除 canvas 上的所有内容，先将变换矩阵重置
        ctx.setTransform();
        ctx.clearRect(0, 0, this.canvas.width, this.canvas.height);
        ctx.restore();
    }


    refresh() {
        this.clear();
        this.drawX();
        this.drawY();
        this.drawMarker();
    }

    drawX() {
        let that = this,
            ctx = this.ctx,
            W = this.W,//放大后的
            H = this.H,
            padding = 0,
            paddingTop = that.paddingTop,
            paddingBottom = that.paddingBottom,
            xLength = W,
            xl = this.xAxis.data.length,//被分成多少段
            xs = xLength / xl;//x每段长度

        Object.assign(ctx, this.xAxisStyle);

        // x轴
        ctx.save();
        ctx.beginPath();
        ctx.setLineDash([])
        ctx.moveTo(0, H - paddingBottom);
        ctx.lineTo(W, H - paddingBottom);
        ctx.stroke();

        this.xAxis.dataShow.forEach((num, i) => {
            var x = xs * (i + 1) + 0.5,
                txt;

            if (that.xAxis) {
                //txt = that.xAxis.formatter.replace('{value}', num);
                txt = num
            }
            ctx.fillText(txt, x, H - paddingBottom + 20);
            ctx.beginPath();
            ctx.lineWidth = 0.5
            ctx.setLineDash([3, 6])
            ctx.strokeStyle = 'gray';
            ctx.moveTo(x, H - paddingBottom);
            ctx.lineTo(x, paddingTop);
            ctx.stroke();
        });
        //绘制（0，0）点的垂直线
        ctx.beginPath();
        ctx.lineWidth = 0.5
        ctx.setLineDash([3, 6])
        ctx.strokeStyle = 'gray';
        ctx.moveTo(0, H - paddingBottom);
        ctx.lineTo(0, 0);
        ctx.stroke();

        ctx.restore();
    }
    drawY() {
        var that = this,
            xLength = this.W,
            yLength = this.H - this.paddingBottom - this.paddingTop,
            yl = this.yAxis.data.length - 1,
            ys = yLength / yl,
            xl = this.xAxis.data.length,//被分成多少段
            xs = xLength / xl,
            ctx = this.ctx;

        ctx.save();
        [...this.yAxis.dataShow].reverse().forEach((num, i) => {
            var y = ys * (i + 1) + 0.5,
                txt;
            ctx.textAlign = 'right';
            if (that.yAxis) {
                //txt = that.yAxis.formatter.replace('{value}', num);
                txt = num
            }
            ctx.fillText(txt, xs - 10, ys * i + this.paddingTop + 20);
            if (i < yl) {//最后一根有x轴代替，不用画了
                ctx.beginPath();
                ctx.lineWidth = 0.5
                ctx.setLineDash([3, 6])
                ctx.strokeStyle = 'gray';
                ctx.moveTo(0, ys * i + this.paddingTop);
                ctx.lineTo(xLength, ys * i + this.paddingTop);
                ctx.stroke();
            }

        });
        ctx.restore();
        //画数据
    }

    /**
     * 原始数据转为坐标轴数据
     * （包括放大缩放的坐标的处理）
     */
    dataToXY(data) {
        let that = this,
            x,
            y,
            //currentScale = that.scale,
            xLength = this.W,
            yLength = (this.H - this.paddingBottom - this.paddingTop),
            xmin = this.xAxis.data[0],//x轴最小值
            xmax = this.xAxis.data.slice(-1)[0],//x轴最大值
            ymin = this.yAxis.data[0],//y轴最小值
            ymax = this.yAxis.data.slice(-1)[0],//y轴最大值
            xl = this.xAxis.data.length,//被分成多少段
            xs = xLength / xl,//x每段长度
            obj = {},//暂存坐标轴数据点
            axisData = [];//坐标轴上的数据
        data.forEach((item, i) => {
            axisData[i] = item
            axisData[i].x = (Math.floor((item.xVal - xmin) / (xmax - xmin) * (xLength - xs)) + xs)  //在坐标系中的x值,多画一列展示数据
            axisData[i].y = (yLength - Math.floor((item.yVal - ymin) / (ymax - ymin) * yLength))   //在坐标系中的y值
            //axisData[i].num = item.num
            //axisData[i].name = item.name

        })

        return axisData;
    }
    /**
     * 输出不通的标记
     */
    createMaker(x, y, type) {
        let ctx = this.ctx,
            img = new Image(),
            that = this,
            makerH = 57,
            makerW = 51,
            selectedMakerW = 117,
            selectedMakerH = 138
        switch (type) {
            case "type_0"://默认产品
                img.src = "data:image/png;base64,"
                break;
            case "type_1"://活动产品
                img.src = "data:image/png;base64,"
                break;
            case "type_2"://持有产品
                img.src = "data:image/png;base64,"
                break;
            case "type_3"://收藏产品
                img.src = "data:image/png;base64,"
                break;
            case "type_4"://选中产品
                img.src = "data:image/png;base64,"
                break;
            default://默认产品
                img.src = "data:image/png;base64,"
                break;
        }
        //console.log("图标的宽度", makerW / that.scale / 2, "对应的缩放比例是", that.scale)
        img.onload = function () {
            if (type == "type_4") {
                ctx.drawImage(this, 0, 0, selectedMakerW, selectedMakerH, (x - selectedMakerW / that.scale / 2), (y - selectedMakerH / that.scale), selectedMakerW / that.scale, selectedMakerH / that.scale)
            } else {
                ctx.drawImage(this, 0, 0, makerW, makerH, (x - makerW / that.scale / 2), (y - makerH / that.scale), makerW / that.scale, makerH / that.scale)
            }
        }

    }
    /**
     * 绘制标记
     */
    drawMarker(pos) {
        let series = this.series,
            ctx = this.ctx,
            //scale放大是对于坐标系的放大，点的实际坐标也相应的放大对于倍数即可
            that = this
        for (let i = 0; i < series.length; i++) {
            let seriesObj = {},//mapDataArr 的每一个元素
                obj = this.dataToXY(series[i].data)  //x,y轴坐标信息
            seriesObj = {
                i: i,//分组序号
                type: "type_" + i,
                data: obj
            }

            for (let j = 0; j < series[i].data.length; j++) {
                if (pos && i == pos.i && j == pos.j) {
                    that.createMaker(obj[j].x, obj[j].y, "type_4")
                } else {
                    that.createMaker(obj[j].x, obj[j].y, seriesObj.type)
                }
                //console.log(j, obj[j].x, obj[j].y, seriesObj.type)
                ctx.beginPath();
                ctx.fillStyle = "red"
                ctx.arc(obj[j].x, obj[j].y, 2, 0, Math.PI * 2, false);
                ctx.fill();

            }
            that.mapDataArr.push(seriesObj);
        }
    }

    clearMap(pos) {
        let that = this,
            obj,
            item,
            ctx = this.ctx;
        this.clear();

        this.drawX();
        this.drawY();
        this.drawMarker(pos);

        if (pos) {
            item = that.mapDataArr[pos.i]
            obj = item.data[pos.j]
            ctx.save();
            //obj是一个点的所有信息
            that.drawCrossLine(obj)
            //点击点的特殊绘制在drawMaker中完成，此处可不处理
            //that.createMaker(obj.x, obj.y, "type_4")
            ctx.restore();
        }
    }
    //画十字线
    drawCrossLine(obj) {
        let ctx = this.ctx,
            that = this,
            H = this.H,
            W = this.W,
            currentScale = that.scale,
            xLength = this.W,
            xl = this.xAxis.data.length,//被分成多少段
            xs = xLength / xl,
            { x, y, xVal, yVal } = obj


        ctx.save()
        ctx.lineWidth = 3
        ctx.strokeStyle = "rgba(252,121,70,0.2)";
        ctx.beginPath()
        ctx.setLineDash([])//切换实线
        ctx.moveTo(x + 0.5, 0)
        ctx.lineTo(x + 0.5, H - that.paddingTop - that.paddingBottom)
        ctx.stroke()
        ctx.moveTo(0, y + 0.5)
        ctx.lineTo(W, y + 0.5)
        ctx.stroke()

        //对应坐标轴的坐标值标注
        //ctx.font = ''
        ctx.fillText(xVal, x, H - that.paddingTop - 10)
        ctx.fillText(yVal, xs - 10, y)



        ctx.restore()

    }


    /**
     * window坐标转canvas坐标
     */
    windowToCanvas(canvas, x, y) {
        let box = canvas.getBoundingClientRect();
        return {
            x: x - box.left - (box.width - canvas.width / 2) / 2,
            y: y - box.top - (box.height - canvas.height / 2) / 2
        };
    }
    animate() {
        var that = this,
            ctx = this.ctx,
            start = new Date(),
            time = 0,
            item, obj, h, r, isStop = true;
        let run = () => {
            ctx.save();
            this.clear();
            // 画坐标系
            that.drawAxis();
            // 画标签
            that.drawTag();
            // 画y轴刻度
            that.drawY();
            ctx.shadowBlur = 1;
            isStop = true;
            time = new Date() - start;
            for (var i = 0, l = that.animateArr.length; i < l; i++) {
                item = that.animateArr[i];
                if (item.hide) continue;

                item.isStop = true;
                ctx.strokeStyle = item.color;
                ctx.shadowColor = item.color;

                for (var j = 0, jl = item.data.length; j < jl; j++) {
                    obj = item.data[j];
                    ctx.beginPath();
                    if (obj.r > obj.radius) {
                        r = obj.r - obj.v * time / 1000;
                        if (r < obj.radius) {
                            obj.r = obj.radius;
                        }
                    } else {
                        r = obj.r + obj.v * time / 1000;
                        if (r > obj.radius) {
                            obj.r = obj.radius;
                        }
                    }
                    if (obj.r != obj.radius) {
                        obj.r = r;
                        item.isStop = false;
                    }
                    if (obj.p > obj.h) {
                        h = obj.y - 10 * time / 1000;
                        if (h < obj.h) {
                            obj.y = obj.p = obj.h;
                        }
                    } else {
                        h = obj.y + 10 * time / 1000;
                        if (h > obj.h) {
                            obj.y = obj.p = obj.h;
                        }
                    }
                    if (obj.y != obj.h) {
                        obj.y = h;
                        item.isStop = false;
                    }
                    var gradient = ctx.createRadialGradient(obj.x, -obj.y, 0, obj.x, -obj.y, obj.r);
                    gradient.addColorStop(0, 'hsla(' + item.hsl + ',70%,85%,0.7)');
                    gradient.addColorStop(1, 'hsla(' + item.hsl + ',70%,60%,0.7)');
                    ctx.fillStyle = gradient;
                    ctx.arc(obj.x, -obj.y, 20, 0, Math.PI * 2, false);
                    ctx.fill();
                    ctx.stroke();
                }
                if (!item.isStop) { isStop = false; }
            }
            ctx.restore();
            if (isStop) {
                return;
            }
            requestAnimationFrame(run);
        };
        run();
    }

}
